import React, { useState } from "react";
import axios from "axios";
import { useEffect } from "react";
import { useParams } from "react-router-dom";

function Read() {

    const [data, setData] = useState([]);
    const { id } = useParams();
    useEffect(() => {

        axios.get('http://localhost:3000/users/' +id)
            .then(res => {
                console.log(res.data);
                setData(res.data)
            })
            .catch(err => {
                console.log(err)
            })
    }, [id]);
    return (
        <React.Fragment>
            <section align-self-center>
            <div className="container d-flex flex-column justify-content-center align-items-center bg-danger shadow">
                <div>
                        <h1 className="text-primary fw-bold">Details of User</h1>
                        </div>
                        <div className="mb-2">
                        <strong>Name: {data.name}</strong>
                        </div>
                        <div className="mb-2">
                        <strong>Email:  {data.email}</strong>
                        </div>
                        <div className="mb-2">
                        <strong>Phone: {data.phone}</strong>
                        </div>
                        </div>

            </section>
        </React.Fragment>
    )
}

export default Read;
