import React, { useState } from "react";
import axios from "axios";
import { useEffect } from "react";
import { useParams } from "react-router-dom";

function Read() {

    const [data, setData] = useState([]);
    const { id } = useParams();
    useEffect(() => {

        axios.get('http://localhost:3000/users/' +id)
            .then(res => {
                console.log(res.data);
                setData(res.data)
            })
            .catch(err => {
                console.log(err)
            })
    }, [id]);
    return (
        <React.Fragment>
            <section className="bg-warning vh-100">
            <div className="container bg-danger shadow d-flex justify-content-center">
                {/* <div className="row d-flex justify-content-center align-items-center">
                    <div className="col"> */}
                        <h2 className="text-primary fw-bold">Details of User</h2>
                        <p>Name: {data.name}<br/>
                           Email:  {data.email}<br/>
                           Phone: {data.phone}</p>
                        {/* </div>
                    </div> */}
            </div>
            </section>

        </React.Fragment>

    )
}

export default Read;
